'\nexport function computeThetas(theta: number) {\n    const cosTheta = Math.cos(theta);\n    const sinTheta = Math.sin(theta);\n    return { cosTheta, sinTheta }\n}\nexport function computeTheta(\n    e: number,\n    E: number\n) {\n    const Œ∏ = 2 * Math.atan2(\n        Math.sqrt(1 + e) * Math.sin(E / 2),\n        Math.sqrt(1 - e) * Math.cos(E / 2)\n    );\n    return Œ∏\n}\nexport function computeInstantaneousOrbitalRadius(\n    p: number,\n    e: number,\n    Œ∏: number\n) {\n    const r = p / (1 + e * Math.cos(Œ∏));\n    return r\n}\nexport function computeCurrentMeanAnomaly(\n    n: number,\n    time: number,\n    M0: number\n) {\n    const M = (n * time + M0) % (2 * Math.PI);\n    return M\n}\nexport function computeNewtonRaphsonCorrection(\n    M: number,\n    e: number,\n    E: number\n) {\n    const dE = (M - E + e * Math.sin(E)) / (1 - e * Math.cos(E));\n    return dE;\n}\nexport function computeEccentricAnomaly(\n    M: number,\n    e: number\n) {\n    let E = M;\n    for (let i = 0; i < 50; i++) {\n        const dE = computeNewtonRaphsonCorrection(M, e, E)\n        E += dE;\n        if (Math.abs(dE) < 1e-9) break;\n    }\n    return E\n}\nexport function computeDistanceToApogee(\n    e: number,\n    a: number,\n    r: number\n) {\n    let dist_to_apo = 0;\n    if (e < 1) {\n        dist_to_apo = a * (1 + e) - r;\n    }\n    return dist_to_apo\n}\n'


