"import React from 'react';\nimport { bodies } from './../constants'\nimport { derivePositionAndVelocityVector } from './utilities';\nimport { computeOfficialPositions } from './../astronomia_utils'\n// Initialize simulation state\nexport const initializeSimulation = (\n  startDate,\n  simOffset,\n  stateRef,\n  setSimTime,\n  probeRef,\n  trailRef,\n  lastTimeRef) => {\n  if (stateRef?.current) {\n    stateRef.current = bodies.map((b, i) => {\n      if (i === 0) return { x: 0, y: 0, vx: 0, vy: 0 };\n      const œâ = Math.sqrt(bodies[0].mu / (b.a ** 3));\n      return { x: b.a, y: 0, vx: 0, vy: œâ * b.a };\n    });\n    probeRef.current = { x: 2e11, y: 0, vx: 0, vy: 3e4 };\n    trailRef.current = [];\n    setSimTime(0);\n    lastTimeRef.current = performance.now();\n  }\n};\n/**\n * Re-initializes all planet & probe state at the chosen start date + offset.\n *\n * @param startDate  The calendar date chosen by the user\n * @param simOffset  Seconds of extra offset (can be negative) from startDate\n * @param stateRef   React ref for planet state array\n * @param setSimTime Setter for simTime (resets to zero)\n * @param probeRef   React ref for your probe object\n * @param trailRef   React ref for your probe trail\n * @param lastTimeRef React ref for performance.now() baseline\n */\nexport function initializeSimulationElapsed(\n  startDate: Date,\n  simOffset: number,\n  stateRef: React.MutableRefObject<any[]>,\n  setSimTime: React.Dispatch<React.SetStateAction<number>>,\n  probeRef: React.MutableRefObject<any>,\n  trailRef: React.MutableRefObject<any[]>,\n  lastTimeRef: React.MutableRefObject<number>,\n  planetTrailsRef?: React.MutableRefObject<any[]>, // Optional if not always present\n  officialTrailsRef?: React.MutableRefObject<any[]>,\n  simTimeRef?: React.MutableRefObject<number>\n) {\n  // 1) compute epoch of J2000 in seconds\n  const epochJ2000 = new Date('2000-01-01T12:00:00Z').getTime() / 1000;\n\n  // 2) compute your chosen start instant in seconds\n  const startEpochSec = (startDate.getTime() / 1000) + simOffset;\n\n  // 3) elapsed seconds since J2000\n  const timeElapsed = startEpochSec - epochJ2000;\n\n  // Use VSOP for initial positions\n  const initialPos = computeOfficialPositions(bodies.slice(1), startDate);\n\n  // Compute velocities via finite difference (small dt for accuracy)\n  const dt_sec = 1; // 1 second; small enough for derivative approximation\n  const futureDate = new Date(startDate.getTime() + dt_sec * 1000);\n  const futurePos = computeOfficialPositions(bodies.slice(1), futureDate);\n\n  stateRef.current = bodies.map((b, i) => {\n    if (i === 0) return { x: 0, y: 0, vx: 0, vy: 0 }; // Sun fixed at origin\n    const pos = initialPos[b.name];\n    const fpos = futurePos[b.name];\n    if (!pos || !fpos) {\n      // Fallback to Keplerian if VSOP fails (unlikely)\n      let M0: any = null\n      if (b?.m0_deg) {\n        M0 = b.m0_deg\n      }\n      const { x, y, vx, vy } = derivePositionAndVelocityVector(\n        b,\n        timeElapsed,\n        M0\n      )\n\n\n      return { x, y, vx, vy };\n    }\n    const vx = (fpos.x - pos.x) / dt_sec;\n    const vy = (fpos.y - pos.y) / dt_sec;\n    return { x: pos.x, y: pos.y, vx, vy };\n  });\n\n  // 5) reset probe & trail\n  probeRef.current = { x: 2e11, y: 0, vx: 0, vy: 3e4 };\n  trailRef.current = [];\n\n  // 6) reset simTime counter and timestamp\n  setSimTime(0);\n  lastTimeRef.current = performance.now();\n\n  // Reset trails if refs provided\n  if (planetTrailsRef) planetTrailsRef.current = bodies.slice(1).map(() => []);\n  if (officialTrailsRef) officialTrailsRef.current = bodies.slice(1).map(() => []);\n  if (simTimeRef) simTimeRef.current = 0;\n}\n\n"


