static inline const AtomInfo *atom_info(int idx) {
    static const AtomInfo UNKNOWN = {"UNK", "X"};
    if (idx < 0 || idx >= ATOM_MAX || ATOM_INFO[idx].atom == NULL) {
        return &UNKNOWN;
    }
    return &ATOM_INFO[idx];
}

