    def add_stage(self, type: StageTypeStr, label: str = None):
        """Add a stage to a batch flow."""
        stage_class = string_to_stage_class(type)
        node = stage_class(self._dag, label=label)
        self._dag.add_node(node)
        return node

    def remove_stage(self, node: Node) -> None:
        """Removes a node from the flow.

        Args:
            node: The node to be removed.
        """
        self._dag.remove_node(node)

    def add_subflow(self, subflow: "Subflow", label: str = None) -> "Subflow":
        """Add a subflow to a batch flow."""
        node = copy.deepcopy(subflow)
        node.label = label
        node._parent_dag = self._dag
        node._parent_fc = self
        return self._dag.add_node(node)

    def create_subflow(
        self,
        label: str,
        nodes: set[Node] | None = None,
        *,
        is_local: bool = True,
        name: str = "",
        description: str = "",
    ) -> "Subflow":
        """Creates a subflow within the flow.

        Args:
            label: Label for the subflow in the flow
            nodes: Set of nodes to create a sublow from. If None, will create an empty
                                                subflow. Defaults to None.
            is_local: If True, will create a local subflow. Will create an global subflow otherwise.
                                       Defaults to True.
            name: Name of the subflow if creating a global subflow. Defaults to "".
            description: Description of the subflow if creating a global subflow. Defaults to "".

        Raises:
            ValueError: if 'name' is set when creating a local subflow
            ValueError: if 'description' is set when creating a local subflow
            ValueError: if 'name' is NOT set when creating a global subflow

        Returns:
            Subflow: subflow object created within the flow
        """
        # need to lazy import to avoid circular dependency
        from ibm_watsonx_data_integration.services.datastage.models.flow.subflow import Subflow

        if nodes is None:
            nodes = set()

        # if is_local, name and description should not be set, a subflow asset is not being created
        if is_local:
            if name:
                raise ValueError("'name' cannot be set when creating a local subflow.")
            if description:
                raise ValueError("'description' cannot be set when creating a local subflow.")
        else:
            if not name:
                raise ValueError("'name' must be set when creating a subflow asset.")

        subflow_dag = self._dag.get_dag_from_nodes(nodes)

        if is_local:
            subflow = Subflow(dag=subflow_dag, label=label, is_local=True, project=self._project, parent_dag=self._dag)
        else:
            subflow = Subflow(
                dag=subflow_dag, project=self._project, name=name, label=label, description=description, is_local=False
            )._create()

        self._dag.replace_nodes_with_supernode(subflow, nodes)
        return subflow

    def deconstruct_subflow(self, subflow: "Subflow") -> None:
        """Deconstructs a local subflow in the flow.

        Args:
            subflow: subflow to desconstruct
        """
        if not subflow.is_local:
            raise ValueError("Can only deconstruct local subflows")

        self._dag._merge(subflow)
        subflow._disable()

    def delete_subflow(self, subflow: "Subflow") -> None:
        """Deletes a local subflow in a flow.

        Args:
            subflow: subflow to delete
        """
        if not subflow.is_local:
            raise ValueError("Can only delete local subflows from a flow. Use project.delete_subflow instead")

        self.remove_stage(subflow)
        subflow._disable()

    def duplicate_subflow(self, subflow: "Subflow", name: str) -> None:
        """Duplicates a local subflow in a flow.

        Args:
            subflow: subflow to duplicate
            name: name of the new subflow

        Returns:
            duplicate_subflow: duplicated subflow
        """
        if not subflow.is_local:
            raise ValueError("Can only duplicate local subflows from a flow. Use project.delete_subflow instead")

        duplicate_subflow = subflow._duplicate(name, "")
        self._dag.add_node(duplicate_subflow)
        return duplicate_subflow
