import math

def корень(число):
    return math.sqrt(число)

def потолок(число):
    return math.ceil(число)

def пол(число):
    return math.floor(число)

def степень(число, степень):
    return math.pow(число, степень)

def синус(число):
    return math.sin(число)

def косинус(число):
    return math.cos(число)

пи = math.pi
