database_encoding:
SELECT
    pg_encoding_to_char(encoding) AS encoding
FROM pg_database WHERE datname = current_database();

profile_read-only:
ALTER DEFAULT PRIVILEGES
    FOR ROLE {grantor}
    IN SCHEMA {schemaname}
    GRANT SELECT
        ON TABLES
        TO {username};
ALTER DEFAULT PRIVILEGES
    FOR ROLE {grantor}
    IN SCHEMA {schemaname}
    GRANT SELECT
        ON SEQUENCES
        TO {username};
ALTER DEFAULT PRIVILEGES
    FOR ROLE {grantor}
    IN SCHEMA {schemaname}
    GRANT EXECUTE
        ON FUNCTIONS
        TO {username};
ALTER DEFAULT PRIVILEGES
    FOR ROLE {grantor}
    IN SCHEMA {schemaname}
    GRANT USAGE
        ON TYPES
        TO {username};
GRANT SELECT
    ON ALL TABLES
    IN SCHEMA {schemaname}
    TO {username};
GRANT SELECT
    ON ALL SEQUENCES
    IN SCHEMA {schemaname}
    TO {username};
GRANT EXECUTE
    ON ALL FUNCTIONS
    IN SCHEMA {schemaname}
    TO {username};

