# Ko'p ishlatiladigan belgilar to'plami
__PUNCT_CHARS = set(list(r"""!"#%&'()*+,-./:;<=>?@[\]^_`{|}~«»—–…·‚„“”‘’‹›"""))


def __is_punct_char(ch: str) -> bool:
    return ch in __PUNCT_CHARS


def __split_token_edge_punct(token: str) -> List[str]:
    """
    Tokenning boshidagi uzluksiz punktuatsiyalar va oxiridagi uzluksiz
    punktuatsiyalarni ajratib, ularni alohida elementlar sifatida qaytaradi.
    Misol: "ichida:" -> ["ichida", ":"]
    Bu funksiya ichki punktuatsiyani o'zgartirmaydi (masalan, "aql-u" ichida '-').
    """
    if token == "":
        return []
    i = 0
    L = len(token)
    while i < L and __is_punct_char(token[i]):
        i += 1
    leading = token[:i] if i > 0 else ""

    j = L - 1
    while j >= 0 and __is_punct_char(token[j]):
        j -= 1
    trailing = token[j+1:] if j < L-1 else ""

    core = token[i:j+1] if j >= i else ""

    parts: List[str] = []
    if leading:
        parts.append(leading)
    if core:
        parts.append(core)
    if trailing:
        parts.append(trailing)
    if not parts:
        return [token]
    return parts


def __normalize_for_match(s: str) -> str:
    """
    So'zlarni ibora mosligi uchun normalizatsiyalash:
    - unicode NFKC
    - kichik harf
    - standart apostroflar
    - faqat lotin harf/raqam/apostrof/chiziqcha qoldiriladi
    """
    if s is None:
        return ""
    s = unicodedata.normalize("NFKC", s)
    s = s.lower()
    s = s.replace("‘", "'").replace("’", "'").replace("`", "'").replace("ʼ", "'").replace("ʻ", "'")
    # olib tashlash faqat moslik uchun; notanish belgilar olib tashlanadi
    s = re.sub(r"[^a-z0-9'\-]+", "", s)
    return s


def __build_trie_from_phrases(phrases: List[str]) -> Dict[str, Any]:
    """
    Iboralar ro'yxatidan trie tuzadi, kalitlar __normalize_for_match bilan normalizatsiyalangan so'zlardir.
    Node: {'...': {...}, '_end': True/False, '_orig': orig_phrase}
    """
    trie: Dict[str, Any] = {}
    for ph in phrases:
        if not ph:
            continue
        words = ph.split()
        node = trie
        for w in words:
            key = __normalize_for_match(w)
            if key == "":
                continue
            if key not in node:
                node[key] = {}
            node = node[key]
        node['_end'] = True
        node['_orig'] = ph
    return trie


def tokenize(text: str, multi_word: bool = False) -> List[str]:
    """
    Matndan tokenlar ro'yxatini qaytaradi.
    multi_word=True bo‘lsa, ibora ichidagi so‘z tokenlari '+' bilan bog‘lanadi (punct alohida).
    """

    trie = __build_trie_from_phrases(PHRASES)

    # avtomatik max phrase length
    max_phrase_len = 1
    for ph in PHRASES:
        if not ph:
            continue
        L = len(ph.split())
        if L > max_phrase_len:
            max_phrase_len = L

    if not text:
        return []

    # 1) Raw whitespace-splitted tokens
    raw_ws_tokens = re.findall(r"\S+", text, flags=re.UNICODE)

    # 2) Split each raw token into [leading_punct, core, trailing_punct] pieces
    elements: List[Dict[str, Any]] = []
    for rt in raw_ws_tokens:
        parts = __split_token_edge_punct(rt)
        # each part: if part is punctuation-only (all chars in punct set) -> is_word=False
        for p in parts:
            is_word = not all(__is_punct_char(ch) for ch in p)
            norm = __normalize_for_match(p) if is_word else ""
            elements.append({'orig': p, 'is_word': is_word, 'norm': norm})

    # 3) Iterate elements and perform greedy match over consecutive word elements
    tokens_out: List[str] = []
    i = 0
    n = len(elements)
    while i < n:
        el = elements[i]
        if not el['is_word']:
            # punctuation (or purely non-word) => push as-is
            tokens_out.append(el['orig'])
            i += 1
            continue

        # attempt greedy match across consecutive word elements
        node = trie
        last_found_len = 0
        j = i
        # We must track positions in element-space; find next word elements up to max_phrase_len words
        k = j
        words_checked = 0
        # Iterate forward collecting word elements
        while k < n and words_checked < max_phrase_len:
            if not elements[k]['is_word']:
                break
            key = elements[k]['norm']
            if key == "":
                break
            if key in node:
                node = node[key]
                words_checked += 1
                k += 1
                if node.get('_end'):
                    last_found_len = words_checked
            else:
                break

        if last_found_len > 0:
            # We have matched last_found_len consecutive word elements starting at i
            # Collect their original 'orig' values (these are core pieces without edge punctuation)
            # Note: We know that matched words are exactly elements[i .. i+last_found_len-1] since punctuation breaks matching.
            matched_parts = [elements[i + t]['orig'] for t in range(last_found_len)]
            if multi_word:
                token_joined = "+".join(matched_parts)
            else:
                token_joined = " ".join(matched_parts)
            tokens_out.append(token_joined)
            i += last_found_len
        else:
            # No phrase matched; output this single original core word
            tokens_out.append(el['orig'])
            i += 1

    return tokens_out
